insert  into `ad_article`(`id`,`title`,`alttitle`,`header`,`body`,`image_path`,`attributes`,`hit_count`,`priority`,`published_time`,`expiredate_time`,`meta`,`keywords`,`author`,`other_link`,`is_active`,`is_hot`,`lang`,`slug`,`category_id`,`created_by`,`updated_by`,`created_at`,`updated_at`) values (146,'Không gian bếp dành cho những người năng động','','Cảm giác sạch sẽ, thông thoáng là tác dụng ảo giác của màu sắc đem lại cho không gian bếp, so với tông màu tối, tạo sự cá tính cho bếp thì với tông màu sáng, cá tính vẫn được trân trọng ngang tầm với sự tao nhã của bếp.','<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">&Ocirc;ng cha ta quan niệm rằng &quot;nh&agrave; sạch th&igrave; m&aacute;t - b&aacute;t sạch ngon cơm&quot; trong văn h&oacute;a ẩm thực v&agrave; trong phong c&aacute;ch sống. Thật sự, sức sống của c&acirc;u n&oacute;i đ&oacute; của &ocirc;ng cha ta vẫn c&ograve;n m&atilde;i lưu truyền đến ng&agrave;y nay.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"http://i.imgur.com/uXyUYd2.jpg\" style=\"height:423px; width:650px\" /></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">S</span></span><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">ở hữu trong tay một gian bếp thật ho&agrave;n hảo l&agrave; điều ước mơ của bao chị em phụ nữ. Một <strong>kh&ocirc;ng gian bếp được trang bị đầy đủ c&aacute;c thiết bị l&agrave;m bếp </strong>ở những vị tr&iacute; th&iacute;ch hợp v&agrave; logic, tạo sự thuận tiện cho c&ocirc;ng việc bếp n&uacute;c. Để cho bếp th&ecirc;m ấm c&uacute;ng hơn cũng như thuận lợi cho việc nấu nướng, ngo&agrave;i trang bị những thiết bị, dụng cụ nấu ăn, c&aacute;c bếp nếu c&oacute; kh&ocirc;ng gian rộng th&ocirc;ng thường c&oacute; th&ecirc;m những tiểu &quot;đảo&quot; ngay tại bếp. Ngo&agrave;i t&aacute;c dụng trang tr&iacute; th&ecirc;m cho kh&ocirc;ng gian bếp, tiểu đảo c&ograve;n th&ecirc;m chức năng vừa l&agrave; b&agrave;n để những thức ăn phục vụ nấu nướng c&ograve;n c&oacute; thể l&agrave; &quot;b&agrave;n d&atilde; chiến&quot; cho những tr&ograve; chuyện giữa người nấu v&agrave; người ngồi.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-tu-nhien1.jpg\" style=\"height:323px; width:650px\" title=\"tủ bếp tự nhiên bằng gỗ sồi Nga có bàn đảo\" /></span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">M&agrave;u sắc sang trọng, trang nh&atilde; v&agrave; thanh lịch, dường như những gam m&agrave;u s&aacute;ng được c&aacute;c nh&agrave; thiết kế lựa chọn tho&aacute;t l&ecirc;n một sắc m&agrave;u vẻ đẹp cho bếp. Cảm gi&aacute;c sạch sẽ, th&ocirc;ng tho&aacute;ng l&agrave; t&aacute;c dụng ảo gi&aacute;c của m&agrave;u sắc đem lại cho kh&ocirc;ng gian bếp, so với t&ocirc;ng m&agrave;u tối, tạo sự c&aacute; t&iacute;nh cho bếp th&igrave; với <strong>t&ocirc;ng m&agrave;u s&aacute;ng</strong>, c&aacute; t&iacute;nh vẫn được tr&acirc;n trọng ngang tầm với sự tao nh&atilde; của bếp, điều đ&oacute; n&oacute;i l&ecirc;n được sở th&iacute;ch cũng như t&acirc;m trạng, t&iacute;nh t&igrave;nh của gia chủ.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"http://i.imgur.com/5MOGZxM.jpg\" style=\"height:488px; width:650px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">Một ng&ocirc;i nh&agrave; sạch l&agrave; điều ai cũng phải l&agrave;m, nhưng để giữ cho bếp sạch sẽ c&oacute; lẽ l&agrave; điều hơi kh&oacute; bởi v&igrave; do đặc th&ugrave; kh&ocirc;ng gian bếp l&agrave; nơi để ch&uacute;ng ta nấu nướng, s&aacute;ng tạo ra những m&oacute;n ăn ngon cho gia đ&igrave;nh. Hơi, m&ugrave;i v&agrave; kh&oacute;i thức ăn lu&ocirc;n được bốc l&ecirc;n t&iacute;ch tụ trong ph&ograve;ng, vật phẩm rơi rớt trong qu&aacute; tr&igrave;nh nấu ăn &nbsp;sẽ l&agrave;m bếp lu&ocirc;n trong t&igrave;nh trạng cần dọn dẹp sạch sẽ hơn bao giờ hết. Giờ đ&acirc;y bạn kh&ocirc;ng cần phải lo lắng về điều đ&oacute;, tủ bếp Thắng Lợi sẽ gi&uacute;p bạn khắc phục những kh&oacute; khăn đ&oacute; một c&aacute;ch dễ d&agrave;ng. V&igrave; bạn l&agrave; <strong>người năng động, c&aacute; t&iacute;nh</strong> n&ecirc;n ch&uacute;ng t&ocirc;i c&oacute; những mẫu <strong>thiết k&ecirc; ri&ecirc;ng</strong> d&agrave;nh cho bạn.</span></span></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"http://i.imgur.com/rbp7eQK.jpg\" style=\"height:650px; width:650px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-tu-nhien1.jpg\" style=\"height:323px; width:650px\" title=\"tủ bếp tự nhiên bằng gỗ sồi Nga có bàn đảo\" /></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p style=\"text-align: justify;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">H&atilde;y li&ecirc;n hệ với ch&uacute;ng t&ocirc;i để được tư vấn miễn ph&iacute; mọi th&ocirc;ng tin bạn cần!</span></span></span></p>\r\n\r\n<p style=\"text-align: justify;\"><em><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><a href=\"http://www.tubepthangloi.com\"><span style=\"color:rgb(0, 0, 255)\">Tủ bếp Thắng Lợi</span></a></span></span></span></em></p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><span style=\"color:rgb(0, 0, 255)\">Hotline: 0984.433.494 - 01633.016.868</span></span></span></span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;</p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">&nbsp;</span></span></span></p>','/ef/a8/8e/585266068f0f6.jpg',NULL,0,0,'2016-12-15 00:00:00',NULL,'Cảm giác sạch sẽ, thông thoáng là tác dụng ảo giác của màu sắc đem lại cho không gian bếp, so với tông màu tối, tạo sự cá tính cho bếp thì với tông màu sáng, cá tính vẫn được trân trọng ngang tầm với sự tao nhã của bếp.','Không gian bếp dành cho những người năng động','','',2,1,'vi','khong-gian-bep-danh-cho-nhung-nguoi-nang-dong',4,23,23,'2016-12-15 16:44:38','2016-12-29 21:45:34'),(145,'Vun đắp yêu thương gia đình trong không gian nhà bếp','','Nơi cả nhà tụ họp, nói chuyện, cười đùa sau một ngày vất vả, mệt mỏi. Và để có được những món ăn ngon thì không thể không nói đến nội thất không gian bếp. Nó sẽ tạo cảm hứng cho các bà nội trợ nấu ra những món ăn ngon.','<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">Bếp ch&iacute;nh l&agrave; <strong>kh&ocirc;ng gian ri&ecirc;ng của b&agrave; nội trợ</strong>. Nơi những người phụ nữa đảm đang cho ra những m&oacute;n ăn ngon cho gia đ&igrave;nh. Nơi cả nh&agrave; tụ họp, n&oacute;i chuyện, cười đ&ugrave;a sau một ng&agrave;y vất vả, mệt mỏi. V&agrave; để c&oacute; được những m&oacute;n ăn ngon th&igrave; kh&ocirc;ng thể kh&ocirc;ng n&oacute;i đến nội thất kh&ocirc;ng gian bếp. N&oacute; sẽ tạo cảm hứng cho c&aacute;c b&agrave; nội trợ nấu ra những m&oacute;n ăn ngon. Ch&iacute;nh v&igrave; thế, việc lựa chọn <strong>tủ bếp</strong>, nội thất cho gian bếp cũng l&agrave;m cho nhiều người phải đau đầu.</span></span></span></p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><strong>Tủ bếp Thắng Lợi</strong> xin chia sẻ một số kiểu d&aacute;ng đẹp ph&ugrave; hợp với mọi gian bếp gia đ&igrave;nh Việt:</span></span></span></p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\">Ph&ograve;ng bếp gia đ&igrave;nh sang chảnh bởi&nbsp;<strong>quầy bar tủ bếp</strong>, với c&aacute;c thiết kế khoe mặt bar, từ ph&ograve;ng kh&aacute;ch nh&igrave;n v&agrave;o tạo được ấn tượng ri&ecirc;ng sự tinh tế, sang trọng của từng kh&ocirc;ng gian bếp.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"http://i.imgur.com/s2y05b4.jpg\" style=\"height:476px; width:650px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><em><span style=\"color:rgb(0, 0, 255)\">Tủ bếp gỗ c&oacute; quầy bar sang chảnh</span></em></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><strong>Tủ bếp c&oacute; b&agrave;n đảo </strong>điển h&igrave;nh chỉ l&agrave; một tủ chứa đồ với bề mặt phẳng. Đơn giản thao t&aacute;c v&agrave; ph&ugrave; hợp với hệ thống tủ b&ecirc;n cạnh. Ph&ugrave; hợp cho từng kh&ocirc;ng gian, tạo sự tiện &iacute;ch tối đa cho ph&ograve;ng bếp.</span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"http://i.imgur.com/x1aDfQz.jpg\" style=\"height:360px; width:650px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><span style=\"color:rgb(0, 0, 255)\"><em>Hệ thống cửa sổ thiết kế h&agrave;i h&ograve;a mang lại cảm gi&aacute;c tho&aacute;ng đ&atilde;ng, sạch sẽ, gọn g&agrave;ng.</em></span></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\">&nbsp;</p>\r\n\r\n<p><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><strong>Tủ bếp chữ L ph&ugrave; hợp với đa số kh&ocirc;ng gian bếp</strong>, với thiết kế gi&uacute;p bạn tận dụng kh&ocirc;ng gian g&oacute;c một c&aacute;ch hiệu quả, khu vực đi lại nấu nướng rộng r&atilde;i, thoải m&aacute;i. Dễ d&agrave;ng điều chỉnh độ d&agrave;i của bếp v&agrave; ph&acirc;n chia khu vực nấu nướng dễ d&agrave;ng. </span></span></span></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><img alt=\"\" src=\"http://i.imgur.com/XdLox8L.jpg\" style=\"height:488px; width:650px\" /></span></span></span></p>\r\n\r\n<p style=\"text-align: center;\"><span style=\"line-height:30px\"><span style=\"font-size:18px\"><span style=\"font-family:times new roman,times,serif\"><span style