insert  into `ad_html`(`id`,`name`,`image_path`,`content`,`is_active`,`lang`,`slug`,`prefix_path`,`created_by`,`updated_by`,`created_at`,`updated_at`) values (40,'Tủ bếp gỗ tự nhiên với bàn đảo sang trọng',NULL,'<p style=\"text-align:justify\">Hiện nay tr&ecirc;n thị trường c&oacute; rất nhiều loại tủ bếp gỗ kh&aacute;c nhau nhằm đ&aacute;p ứng tối đa nhu cầu của kh&aacute;ch h&agrave;ng v&agrave; sự ph&ugrave; hợp với những gian bếp kh&aacute;c nhau. Những sản phẩm tự nhi&ecirc;n c&oacute; lẽ lu&ocirc;n d&agrave;nh được sự ưu ti&ecirc;n h&agrave;ng đầu của người ti&ecirc;u d&ugrave;ng. Thật vậy<strong> <a href=\"http://tubepthangloi.com/index.php/chuyen-muc/tu-bep-go-tu-nhien\" target=\"_blank\" title=\"tu bep tu nhien\">tủ bếp gỗ tự nhi&ecirc;n</a></strong> lu&ocirc;n được mọi người ưu ti&ecirc;n lựa chọn h&agrave;ng đầu<br />\r\nBởi n&oacute; kh&ocirc;ng chỉ mang đến vẻ đẹp tự nhi&ecirc;n, tạo sự sang trọng m&agrave; c&ograve;n c&oacute; độ bền cao, kh&ocirc;ng thấm nước v&agrave; chống mối mọi l&agrave; điều m&agrave; nhiều người hay lo ngại</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-tu-nhien.jpg\" style=\"height:397px; width:500px\" title=\"tủ bếp gỗ tự nhiên với thiết kế sang trọng\" /></p>\r\n\r\n<p>Với những mẫu tủ bếp&nbsp;đi k&egrave;m với b&agrave;n đảo sang trọng, khiến cho gian bếp của gia đ&igrave;nh trở n&ecirc;n sang trọng, ấm &aacute;p v&agrave; cả sự tiện nghi cho c&aacute;c b&agrave; nội chợ, đảm bảo đầy đủ tiện nghi với nhiều gian tủ để bạn c&oacute; thể để nhiều loại đồ d&ugrave;ng kh&aacute;c nhau trong gian bếp.<br />\r\nThiết kế hiện đại theo hơi hướng phướng T&acirc;y tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng, ấn tượng cho căn bếp của gia chủ. Ngo&agrave;i ra bạn c&oacute; thể lựa chọn nhiều kiểu d&aacute;ng tủ gỗ kh&aacute;c nhau sao cho ph&ugrave; hợp nhất với gian bếp nh&agrave; bạn.<br />\r\nH&atilde;y c&ugrave;ng tham khảo những gian mẫu <strong>tủ bếp gỗ tự nhi&ecirc;n</strong> dưới đ&acirc;y để cảm nhận sự tiện nghi, c&ugrave;ng t&iacute;nh thẩm mỹ cho kh&ocirc;ng gian bếp nh&agrave; m&igrave;nh</p>\r\n\r\n<p style=\"text-align:center\"><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-tu-nhien1.jpg\" style=\"height:323px; width:500px\" title=\"tủ bếp tự nhiên bằng gỗ sồi Nga có bàn đảo\" /></p>\r\n\r\n<p>Ngo&agrave;i ra c&ograve;n rất nhiều điều th&uacute; vị kh&aacute;c đang chờ bạn khi đến với tủ bếp thắng lợi. Bạn c&oacute; thể chi&ecirc;m ngưỡng nhiều mẫu thiết kế tủ bếp kh&aacute;c nhau tạo cảm hứng thiết kế cho kh&ocirc;ng gian bếp nh&agrave; m&igrave;nh. Li&ecirc;n hệ ngay với ch&uacute;ng t&ocirc;i để nhận những ưu đ&atilde;i bất ngờ.</p>',1,'vi','tu-bep-go-tu-nhien-voi-ban-dao-sang-trong','category_news',1,1,'2016-09-11 22:58:04','2016-09-11 23:06:14'),(41,'Các kiểu tủ bếp gỗ Veneer rất được yêu thích hiện nay',NULL,'<p>N&oacute;i đến c&aacute;c d&ograve;ng tủ bếp được y&ecirc;u th&iacute;ch hiện nay, phải kể đến <a href=\"http://tubepthangloi.com/index.php/chuyen-muc/tu-bep-go-verneer\" target=\"_blank\" title=\"tu bep go veneer\"><strong>tủ bếp gỗ Veneer</strong></a> với nhiều mẫu m&atilde; kiểu d&aacute;ng kh&aacute;c nhau, thiết kế đa dạng sang trọng ph&ugrave; hợp với mọi gian bếp của gia đ&igrave;nh bạn. Từ những chất liệu gỗ tự nhi&ecirc;n dưới b&agrave;n tay của những nghệ nh&acirc;n đ&atilde; mang đến c&aacute;c sản phẩm nh&agrave; bếp tuyệt vời được nhiều người y&ecirc;u th&iacute;ch từ những kiểu d&aacute;ng truyền thống đến những tủ bếp c&aacute;ch t&acirc;n mang hơi hướng phương t&acirc;y, tạo n&ecirc;n phong c&aacute;ch ri&ecirc;ng cho kh&ocirc;ng gian bếp. Với mục đ&iacute;ch kh&ocirc;ng chỉ mang đến sự tiện &iacute;ch cho c&aacute;c b&agrave; nội chợ m&agrave; c&ograve;n c&oacute; t&iacute;nh thẩm mũ cao. H&atilde;y c&ugrave;ng kh&aacute;m ph&aacute; 1 số mẫu tủ bếp Veneer hot nhất hiện nay.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-veneer.jpg\" style=\"height:361px; width:500px\" title=\"tủ bếp gỗ Veneer hiện đại sang trọng\" /></p>\r\n\r\n<p>1. Thiết kế tủ bếp h&igrave;nh chữ L, h&igrave;nh chữ I<br />\r\nĐ&acirc;y l&agrave; kiểu d&aacute;ng kh&aacute; phổ biến hiện nay, vừa đơn giản m&agrave; lại kh&aacute; đẹp mắt. dễ th&iacute;ch nghi với kh&ocirc;ng gian bếp m&agrave; cẫn đảm bảo c&aacute;c c&ocirc;ng năng sử dụng.<br />\r\nKiểu tủ bếp chữ I chiếm được t&igrave;nh cảm của người ti&ecirc;u d&ugrave;ng bởi những thiết kế thanh mảnh, họa tiết đơn giản nhưng lại c&oacute; điểm nhấn ở m&agrave;u sắc tạo n&ecirc;n sự ph&aacute; c&aacute;ch nổi bật cho nh&agrave; bếp<br />\r\nKh&aacute;c với tủ chữ I, tủ bếp chữ L lại mang đến cho người sử dụng nhiều sự tiện nghi hơn vẫn tr&ecirc;n nền tảng chủ đạo l&agrave; thiết kế nhẹ nh&agrave;ng với sự h&ograve;a trộn của 2 game m&agrave;u xanh, trắng. Đặc biệt c&oacute; thể bố tr&iacute; gần cửa sổ khiến cho ph&ograve;ng bếp trở n&ecirc;n tho&aacute;ng đ&atilde;ng, tận dụng được &aacute;nh s&aacute;ng tự nhi&ecirc;n. Bạn kh&ocirc;ng phải lo ngại về m&ugrave;i ph&ograve;ng bếp khi đun nấu.<br />\r\n2. Thiết kế h&igrave;nh chữ U, h&igrave;nh chữ G</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/Tu-bep-go-Veneer-kieu-chu-U.jpg\" style=\"height:265px; width:500px\" title=\"tủ bếp gỗ Veneer hình chữ U\" /></p>\r\n\r\n<p>Ngo&agrave;i chữ L v&agrave; I, <strong>tủ bếp gỗ Veneer</strong> c&ograve;n c&oacute; sự ph&aacute; c&aacute;ch ở kiểu d&aacute;ng chữ U hay chữ G, mang lại sự ấm &aacute;p cho ng&ocirc;i nh&agrave; v&agrave; sự đột ph&aacute; trong s&aacute;ng tạo kiểu d&aacute;ng. Mẫu thiết kế tủ bếp h&igrave;nh chữ U dưới đ&acirc;y với sự c&aacute;ch t&acirc;n ở 2 b&ecirc;n c&aacute;nh ngắn l&agrave;m cho gian bếp th&ecirc;m phần độc đ&aacute;o, tinh tế.<br />\r\n3. Thiết kế tủ bếp c&oacute; b&agrave;n đảo, quầy bar<br />\r\nĐi k&egrave;m với tủ bếp l&agrave; kh&ocirc;ng gian nấu ăn thuận tiện. Ch&iacute;nh v&igrave; thế m&agrave; tủ bếp b&agrave;n đảo, hay quầy bar &nbsp;ra đời. N&oacute; đang l&agrave; một xu hướng mới hiện nay. Ưu điểm của n&oacute; nằm ở sự linh hoạt. Bạn c&oacute; thể t&ugrave;y &yacute; đặt vị tr&iacute; b&agrave;n đảo theo sở th&iacute;ch của m&igrave;nh vừa l&agrave; để trang tr&iacute; vừa l&agrave; nơi bạn c&oacute; thể nấu nướng hay thưởng thức c&aacute;c m&oacute;n ăn.<br />\r\nH&atilde;y đến ngay c&aacute;c showroom của <strong>tủ bếp gỗ Veneer</strong> Thắng lợi để lựa chọn cho m&igrave;nh sản phẩm ph&ugrave; hợp nhất cho gian bếp của gia đ&igrave;nh bạn, ngo&agrave;i ra c&ograve;n nhận được những ưu đ&atilde;i, khuyến m&atilde;i bất ngờ.</p>',1,'vi','cac-kieu-tu-bep-go-veneer-rat-duoc-yeu-thich-hien-nay','category_news',1,1,'2016-09-11 23:10:27','2016-09-11 23:10:27'),(42,'Ưu điểm nổi bật của các loại tủ bếp gỗ Laminate',NULL,'<p>Nếu c&aacute;c d&ograve;ng tủ bếp g&otilde; tự nhi&ecirc;n mang đến c&aacute;c sản phẩm với chất liệu tự nhi&ecirc;n th&igrave; <a href=\"http://tubepthangloi.com/index.php/chuyen-muc/tu-bep-go-laminale\" target=\"_blank\" title=\"tu bep go laminale\"><strong>tủ bếp gỗ Laminate</strong></a> lại được người ti&ecirc;u d&ugrave;ng y&ecirc;u th&iacute;ch bởi kiểu d&aacute;ng đẹp mắt, gọn nhẹ đa m&agrave;u sắc<br />\r\nLaminate l&agrave; 1 sản phẩm của c&ocirc;ng nghiệp in m&agrave;u cao cấp tr&ecirc;n giấy Graft. C&oacute; rất nhiều sự lựa chọn kh&aacute;c nhau cho bạn về cả m&agrave;u sắc lần kiểu d&aacute;ng như m&agrave;u trơn, v&acirc;n đ&aacute;, v&acirc;n gỗ hay kim loại m&agrave;u, &aacute;nh nhũ&hellip;. Những hoa văn họa tiết được thiết kiết ri&ecirc;ng như bề mặt mịn, mờ hay v&acirc;n nổi m&agrave; ở gỗ tự nhi&ecirc;n kh&ocirc;ng thể c&oacute; được.</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-laminale.jpg\" style=\"height:375px; width:500px\" title=\"Tủ bếp gỗ laminale kiểu dáng đẹp mắt sang trọng\" /></p>\r\n\r\n<p><strong>Tủ bếp gỗ Laminate</strong> gi&uacute;p c&aacute;c nh&agrave; thiết kể thể hiện tối đa sự s&aacute;ng tạo của m&igrave;nh m&agrave; kh&ocirc;ng k&eacute;m phần sang trọng. Ưu điểm nổi bật của loại bếp n&agrave;y ch&iacute;nh l&agrave; khả năng chống xước, chống v&agrave; đập mối mọt hay kh&aacute;ng ẩm. Đ&acirc;y c&oacute; lẽ l&agrave; những tiến bộ vượt bậc so với mong đợi của người d&ugrave;ng,<br />\r\nTubepthangloi.com xin giới thiệu đến c&aacute;c bạn những mẫu thiết kế <strong>tủ bếp gỗ Laminate</strong>, được sản xuất tr&ecirc;n d&acirc;y chuyền hiện đại c&ugrave;ng đội ngũ nh&acirc;n vi&ecirc;n thiết kế s&aacute;ng tạo.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/tu-bep-go-laminale-ban-dao.jpg\" style=\"height:345px; width:500px\" title=\"Tủ bếp gỗ laminale có bàn đảo\" /></p>\r\n\r\n<p>Kiểu d&aacute;ng chữ L gọn nhẹ, th&acirc;n thiện với kh&ocirc;ng gian bếp, c&oacute; thể ph&ugrave; hợp với cả những gian bếp c&oacute; diện t&iacute;ch nhỏ hẹp. Để hạn chế nhược điểm n&agrave;y của gian bếp, tủ được thiết kế với nhiều ngăn tủ kh&aacute;c nhau, bạn c&oacute; thể bố tr&iacute; nhiều loại vật dụng kh&aacute;c nhau<br />\r\nĐược sản xuất tr&ecirc;n d&acirc;y truyền hiện đại, sử dụng chất liệu gỗ &eacute;p c&ocirc;ng nghiệp, <strong>tủ bếp gỗ Laminate</strong> khắc phục được mọi nhược điểm của gỗ tự nhi&ecirc;n c&ugrave;ng với đ&oacute; l&agrave; khả năng chống mối mọt ẩm mốc, kh&ocirc;ng sợ bị cong v&ecirc;nh hoặc nứt nẻ do thay đổi thời tiết. C&oacute; khả năng chịu nhiệt, bạn kh&ocirc;ng sợ bị ảnh hưởng của nhiệt độ cao khi nấu nướng.<br />\r\nC&ugrave;ng với đ&oacute; l&agrave; m&agrave;u sắc sang trọng thanh lịch được phủ bởi lớp sơn cao cấp bảo vệ bề mặt tủ, mang đến t&iacute;nh thẩm mỹ tối đa cho căn bếp nh&agrave; bạn<br />\r\nNgo&agrave;i ra c&ograve;n rất nhiều sản phẩm vượt trội kh&aacute;c ở Thắng lợi. Nhanh tay đến với c&aacute;c showroom của tubepthangloi.com để tiện nghi hơn cho gian bếp nh&agrave; bạn.</p>',1,'vi','uu-diem-noi-bat-cua-cac-loai-tu-bep-go-laminate','category_news',1,1,'2016-09-11 23:54:41','2016-09-11 23:54:41'),(43,'Mẹo lựa chọn máy rửa bát tốt nhất cho gia đình',NULL,'<p>Cuộc sống hiện đại khiến con người lu&ocirc;n bận rộn với c&ocirc;ng việc học tập. Sự ph&aacute;t triển của khoa học c&ocirc;ng nghệ mang đến những sản phẩm tiện &iacute;ch, l&agrave;m giảm sức lao động cuả con người. <a href=\"http://tubepthangloi.com/index.php/chuyen-muc/may-rua-bat\" target=\"_blank\" title=\"may rua bat\"><strong>M&aacute;y rửa b&aacute;t</strong></a> l&agrave; một trong những th&agrave;nh c&ocirc;ng phải kể đến. Gi&uacute;p c&aacute;c b&agrave; nội trợ giảm đi những g&aacute;nh nặng việc nh&agrave;, v&agrave; nỗi lo bị hại do tay do dầu rửa b&aacute;t.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/uploads/ckfinder/images/may-rua-bat.jpg\" style=\"height:561px; width:500px\" title=\"Máy rửa bát với những tiện ích bất ngờ\" /></p>\r\n\r\n<p><strong>M&aacute;y rửa b&aacute;t</strong> gi&uacute;p chị em tiết kiệm được rất nhiều thời gian l&agrave;m việc nh&agrave;.<br />\r\nƯu điểm của m&aacute;y l&agrave; ph&ugrave; hợp với những gia đ&igrave;nh đ&ocirc;ng đ&uacute;c, v&agrave;o những dịp lễ tết, b&aacute;t đũa được sử dụng rất nhiều. Tuy nhi&ecirc;n m&aacute;y lại c&oacute; k&iacute;ch thước kh&aacute; lớn n&ecirc;n chiếm nhiều diện t&iacute;ch. Đ&ograve;i hỏi gia đ&igrave;nh phải c&oacute; kh&ocirc;ng gian bếp đủ rộng.<br />\r\nĐể khắc phục nhược điểm n&agrave;y, hiện nay đ&atilde; c&oacute; một số loại <strong>m&aacute;y rửa b&aacute;t</strong> để b&agrave;n c&oacute; k&iacute;ch thước nhỏ gọn hơn, ph&ugrave; hợp với những kh&ocirc;ng gian nhỏ hẹp. Trong đ&oacute;, bề ngang của m&aacute;y dao động từ 30 - 45 cm thường v&agrave; chỉ c&oacute; 1 ngăn. Sử dụng rửa tối đa được khoảng 15 loại đồ d&ugrave;ng. V&igrave; vậy loại m&aacute;y n&agrave;y chỉ d&ugrave;ng phổ biến cho những gia đ&igrave;nh &iacute;t người.<br />\r\nLựa chọn được chiếc m&aacute;y&nbsp;ph&ugrave; hợp th&igrave; vị tr&iacute; đặt m&aacute;y cũng l&agrave; một vấn đề cần được quan t&acirc;m. Th&ocirc;ng thường m&aacute;y n&agrave;y&nbsp;lu&ocirc;n được ưu ti&ecirc;n đặt trong gian bếp , gần tủ b&aacute;t để dễ d&agrave;ng thuận tiện trong việc dọn dẹp.&nbsp;<br />\r\nNgười d&ugrave;ng cần lưu &yacute; khi sử dụng m&aacute;y, trước khi cho b&aacute;t đĩa v&agrave;o m&aacute;y bạn phải gạt bỏ hết thức ăn thừa v&agrave; n&ecirc;n xếp ch&uacute;ng theo chiều nghi&ecirc;ng để m&aacute;y rửa sạch dễ d&agrave;ng.<br />\r\nM&aacute;y khi&nbsp;hoạt động tốn kh&aacute; nhiều nước để l&agrave;m sạch, v&igrave; thế bạn n&ecirc;n gom b&aacute;t đũa vừa đủ với dung lượng của m&aacute;y, để giảm chi ph&iacute;. C&ugrave;ng kh&ocirc;ng n&ecirc;n bỏ xong chảo, c&ugrave;ng c&aacute;c vật dụng c&oacute; nhiều hoa văn sẽ khiến hoa văn nhanh mờ.<br />\r\nTr&ecirc;n đ&acirc;y l&agrave; những tiện &iacute;ch cũng như những lưu &yacute; cho c&aacute;c b&agrave; nội chợ khi chọn mua <strong>m&aacute;y rửa b&aacute;t</strong> cho gia đ&igrave;nh m&igrave;nh. H&atilde;y đến ngay với tubepthangloi.com để hiểu r&otilde; hơn về sản phẩm. Ch&uacute;c bạn sớm lựa chọn được sản phẩm ưng &yacute; cho gian bếp gia đ&igrave;nh m&igrave;nh.&nbsp;</p>',1,'vi','meo-lua-chon-may-rua-bat-tot-nhat-cho-gia-dinh','category_news',1,1,'2016-09-11 23:55:12','2016-09-11 23:55:12');

